// src/App.js
import React, { useState } from 'react';
import Layout from './components/Layout';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';


export default function App() {
  // State to track the current active route. It defaults to '/' (the home page).
  const [route, setRoute] = useState('/');

  // This function determines which page component to render based on the current 'route' state.
  const renderContent = () => {
    switch (route) {
      case '/about':
        return <AboutPage />;
      case '/contact':
        return <ContactPage />;
      case '/':
      default:
        return <HomePage />;
    }
  };

  return (
    // The Layout component wraps every page.
    // We pass the current route and the function to update it (setRoute) as props.
    <Layout route={route} setRoute={setRoute}>
      {/* The result of renderContent() is passed as a child to Layout,
          so it will be displayed in the main content area. */}
      {renderContent()}
    </Layout>
  );
}
