import React from 'react';

// NavLink is a reusable component for navigation links.
// It receives props to determine its destination, active state, and click behavior.
const NavLink = ({ to, active, onClick, children, icon }) => (
  <a
    href={`#${to}`} // Using href with a hash for semantic correctness, though we prevent default behavior.
    onClick={(e) => {
      e.preventDefault(); // Prevents the browser from following the link and reloading the page.
      onClick(to); // Calls the setRoute function passed from App.js to update the state.
    }}
    className={`flex items-center px-4 py-2 text-sm font-medium rounded-md transition-colors duration-200 ${
      active
        ? 'bg-indigo-600 text-white shadow-md' // Styles for the active link
        : 'text-gray-600 dark:text-gray-300 hover:bg-gray-200 dark:hover:bg-gray-700' // Styles for inactive links
    }`}
  >
    {icon} {/* Renders the icon passed as a prop */}
    <span className="ml-3">{children}</span> {/* Renders the text content of the link */}
  </a>
);

export default NavLink;
