// src/components/Layout.js
import React from 'react';
// 1. Import Outlet and useLocation
import { Outlet, useLocation } from 'react-router-dom';
import { Home as HomeIcon, Info, Mail, ChevronRight } from 'lucide-react';
import NavLink from './NavLink';

// 2. We no longer need route or setRoute props
const Layout = ({ children }) => {
  const location = useLocation(); // 3. Get the current location
  const currentPath = location.pathname;

  return (
    <div className="min-h-screen flex flex-col bg-gray-100 dark:bg-gray-900 font-sans text-gray-900 dark:text-white">
      <header className="bg-white dark:bg-gray-800 shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <span className="font-bold text-xl text-indigo-600 dark:text-indigo-400">React Route Demo</span>
            </div>
            <nav className="hidden md:flex items-center space-x-4">
              {/* 4. NavLink now works without extra props */}
              <NavLink to="/" icon={<HomeIcon size={18} />}>Home</NavLink>
              <NavLink to="/about" icon={<Info size={18} />}>About</NavLink>
              <NavLink to="/contact" icon={<Mail size={18} />}>Contact</NavLink>
            </nav>
          </div>
        </div>
      </header>

      <main className="flex-grow max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8 w-full">
        <div className="flex items-center text-sm text-gray-500 dark:text-gray-400 mb-4">
            <span>Pages</span>
            <ChevronRight size={16} className="mx-1" />
            {/* 5. Update breadcrumb to use currentPath */}
            <span className="font-semibold text-gray-700 dark:text-gray-200 capitalize">{currentPath === '/' ? 'Home' : currentPath.substring(1)}</span>
        </div>
        <Outlet /> {/* 6. This is where the page components will be rendered */}
      </main>
      
       <footer className="bg-white dark:bg-gray-800 mt-auto">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8 text-center text-gray-500 dark:text-gray-400 text-sm">
          <p>&copy; {new Date().getFullYear()} React Route Demo. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Layout;
